'use strict';

var linkedinScraper = require('linkedin-scraper');
var express = require('express');

var exportObject = {};

exportObject.getMyStuff = function(url, callback){

var finalObject = {};

linkedinScraper.getProfile(url, function (response){

	var companyPageURL = response.companyURL; 
	var name = response.name.split(" ");
	var linksArray = response.linksArray;

	//console.log(linksArray);
	//console.log(linksArray);
	//var linksArray = response.linksArray;

	finalObject.firstName = name[0];
	finalObject.lastName = name[(name.length-1)];
	finalObject.linksArray = linksArray;

	linkedinScraper.getWebsite(companyPageURL, function (response){
		finalObject.companyURL = response.website;
		callback(finalObject);
	});
});

}


module.exports = exportObject;








